 const hoverReducer = (store = { user: {} }, action) => {

    switch (action.type) {
        case actions.FETCH_USERDATA: 
            const state = { ...store, user : {...store.user, ...action.payload}}
            const is = ( state == store )
            console.log("reducer",is,state)
            return state
